mapper.select:
SELECT 

FROM three_shoe_info a

WHERE a.id = #{id}

SELECT 

FROM three_shoe_info a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.xlh = #{xlh}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM three_shoe_info a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE three_shoe_info SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
